# –°–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤–∏–µ –±—É–∫–≤ –∞—Ä—Ö–µ—Ç–∏–ø–∞–º
answers = {
    "A": "–ú—É–¥—Ä–µ—Ü",
    "B": "–í–æ–∏–Ω",
    "C": "–¢–≤–æ—Ä–µ—Ü",
    "D": "–ó–∞–±–æ—Ç–ª–∏–≤—ã–π",
    "E": "–õ–∏–¥–µ—Ä",
    "F": "–ò—Å–∫–∞—Ç–µ–ª—å",
    "G": "–ú–∞–≥",
    "H": "–ì–µ–¥–æ–Ω–∏—Å—Ç"
}

# –û–ø–∏—Å–∞–Ω–∏–µ –∞—Ä—Ö–µ—Ç–∏–ø–æ–≤
archetypes = {
    "–ú—É–¥—Ä–µ—Ü": "–¢—ã —Å—Ç—Ä–µ–º–∏—à—å—Å—è –∫ –∑–Ω–∞–Ω–∏—è–º –∏ –ø–æ–Ω–∏–º–∞–Ω–∏—é –º–∏—Ä–∞. –°–∏–ª–∞ ‚Äì –≤ —Ä–∞–∑—É–º–µ.",
    "–í–æ–∏–Ω": "–¢—ã –±–æ—Ä–µ—Ü, –∫–æ—Ç–æ—Ä—ã–π –∑–∞—â–∏—â–∞–µ—Ç —Å–≤–æ–∏ —Ü–µ–Ω–Ω–æ—Å—Ç–∏ –∏ –∏–¥—ë—Ç –≤–ø–µ—Ä—ë–¥.",
    "–¢–≤–æ—Ä–µ—Ü": "–¢—ã –≤–¥–æ—Ö–Ω–æ–≤–ª—è–µ—à—å –º–∏—Ä —á–µ—Ä–µ–∑ —Ç–≤–æ—Ä—á–µ—Å—Ç–≤–æ –∏ –∏–¥–µ–∏.",
    "–ó–∞–±–æ—Ç–ª–∏–≤—ã–π": "–¢—ã –¥–∞—Ä–∏—à—å —Ç–µ–ø–ª–æ –∏ –∑–∞–±–æ—Ç—É, —Ç–≤–æ—è —Å–∏–ª–∞ ‚Äì –≤ –ø–æ–¥–¥–µ—Ä–∂–∫–µ.",
    "–õ–∏–¥–µ—Ä": "–¢—ã —É–º–µ–µ—à—å –≤–µ—Å—Ç–∏ –∑–∞ —Å–æ–±–æ–π –∏ –≤–¥–æ—Ö–Ω–æ–≤–ª—è—Ç—å –ª—é–¥–µ–π.",
    "–ò—Å–∫–∞—Ç–µ–ª—å": "–¢—ã –≤–µ—á–Ω–æ –∏—â–µ—à—å –Ω–æ–≤—ã–µ –ø—É—Ç–∏, —Å–≤–æ–±–æ–¥–∞ ‚Äì —Ç–≤–æ—è —Ü–µ–Ω–Ω–æ—Å—Ç—å.",
    "–ú–∞–≥": "–¢—ã –ø—Ä–µ–æ–±—Ä–∞–∑—É–µ—à—å –º–∏—Ä, —Å–æ–µ–¥–∏–Ω—è—è –Ω–µ–≤–∏–¥–∏–º–æ–µ –∏ –≤–∏–¥–∏–º–æ–µ.",
    "–ì–µ–¥–æ–Ω–∏—Å—Ç": "–¢—ã –Ω–∞—Å–ª–∞–∂–¥–∞–µ—à—å—Å—è –∂–∏–∑–Ω—å—é –∏ —É—á–∏—à—å —ç—Ç–æ–º—É –¥—Ä—É–≥–∏—Ö."
}

# –î–∞–Ω–Ω—ã–µ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π
user_data = defaultdict(lambda: {"step": 0, "counts": defaultdict(int)})

@dp.message_handler(commands=["start"])
async def start(message: types.Message):
    user_id = message.from_user.id
    user_data[user_id]["step"] = 0
    user_data[user_id]["counts"].clear()
    await message.answer("–ü—Ä–∏–≤–µ—Ç üå∏\n–î–∞–≤–∞–π —É–∑–Ω–∞–µ–º —Ç–≤–æ–π –∞—Ä—Ö–µ—Ç–∏–ø!\n–û—Ç–≤–µ—á–∞–π –Ω–∞ –≤–æ–ø—Ä–æ—Å—ã, –≤—ã–±–∏—Ä–∞—è A‚ÄìH.")
    await send_question(user_id, message)

async def send_question(user_id, message):
    step = user_data[user_id]["step"]
    if step < len(questions):
        keyboard = types.ReplyKeyboardMarkup(resize_keyboard=True, one_time_keyboard=True)
        keyboard.add("A", "B", "C", "D", "E", "F", "G", "H")
        await bot.send_message(user_id, questions[step], reply_markup=keyboard)
    else:
        await show_result(user_id, message)

@dp.message_handler(lambda message: message.text.upper() in answers.keys())
async def handle_answer(message: types.Message):
    user_id = message.from_user.id
    step = user_data[user_id]["step"]
    if step < len(questions):
        choice = message.text.upper()
        archetype = answers[choice]
        user_data[user_id]["counts"][archetype] += 1
        user_data[user_id]["step"] += 1
        await send_question(user_id, message)
    else:
        await show_result(user_id, message)

async def show_result(user_id, message):
    counts = user_data[user_id]["counts"]
    if not counts:
        await message.answer("–¢—ã –Ω–µ –æ—Ç–≤–µ—Ç–∏–ª –Ω–∏ –Ω–∞ –æ–¥–∏–Ω –≤–æ–ø—Ä–æ—Å üòÖ")
        return
    result = max(counts, key=counts.get)
    description = archetypes[result]
    await message.answer(f"‚ú® –¢–≤–æ–π –∞—Ä—Ö–µ—Ç–∏–ø: *{result}*\n\n{description}", parse_mode="Markdown")
    user_data[user_id]["step"] = 0
    user_data[user_id]["counts"].clear()

if __name__ == "__main__":
    print("‚úÖ –ù–æ–≤—ã–π –∫–æ–¥ —É—Å–ø–µ—à–Ω–æ –∑–∞–ø—É—â–µ–Ω")
    executor.start_polling(dp, skip_updates=True)